SELECT DISTINCT `DRP_modelcontainer`.`id`, COUNT(`DRP_modelcontainer_numRxnDescriptors`.`numrxndescriptor_id`) AS `num_numDescs`, COUNT(`DRP_modelcontainer_boolRxnDescriptors`.`boolrxndescriptor_id`) AS `num_boolDescs` FROM `DRP_modelcontainer` INNER JOIN `DRP_modelcontainer_numRxnDescriptors` ON ( `DRP_modelcontainer`.`id` = `DRP_modelcontainer_numRxnDescriptors`.`modelcontainer_id` ) INNER JOIN `DRP_modelcontainer_boolRxnDescriptors` ON ( `DRP_modelcontainer`.`id` = `DRP_modelcontainer_boolRxnDescriptors`.`modelcontainer_id` ) WHERE (`DRP_modelcontainer`.`built` = True AND (`DRP_modelcontainer_numRxnDescriptors`.`numrxndescriptor_id`) IN (SELECT U0.`numericdescriptor_ptr_id` FROM `DRP_numrxndescriptor` U0 INNER JOIN `DRP_numericdescriptor` U1 ON ( U0.`numericdescriptor_ptr_id` = U1.`descriptor_ptr_id` ) INNER JOIN `DRP_descriptor` U2 ON ( U1.`descriptor_ptr_id` = U2.`id` ) WHERE U2.`heading` IN (reaction_temperature, reaction_time, reaction_pH, teflon_pouch)) AND (`DRP_modelcontainer_boolRxnDescriptors`.`boolrxndescriptor_id`) IN (SELECT U0.`booleandescriptor_ptr_id` FROM `DRP_boolrxndescriptor` U0 INNER JOIN `DRP_booleandescriptor` U1 ON ( U0.`booleandescriptor_ptr_id` = U1.`descriptor_ptr_id` ) INNER JOIN `DRP_descriptor` U2 ON ( U1.`descriptor_ptr_id` = U2.`id` ) WHERE U2.`heading` IN (reaction_temperature, reaction_time, reaction_pH, teflon_pouch))) GROUP BY `DRP_modelcontainer`.`id` HAVING COUNT(`DRP_modelcontainer_numRxnDescriptors`.`numrxndescriptor_id`) = 3 ORDER BY NULL
